import React from 'react';
import ReactDOM from 'react-dom';

class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
    };
  }
  mySubmitHandler = (event) => {
    event.preventDefault();

      // let username = this.state.username;
      // var validcharacters = '1234567890abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
      // for (var i = 0, l = username.length; i < l; ++i) {
      //     if (validcharacters.indexOf(username.substr(i, 1)) == -1) {
      //         alert("Username must contain only numbers and letters");
      //     }
      // }

    let age = this.state.age;
    if (!Number(age)) {
      alert("Your age must be a number");
    }
  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({[nam]: val}); // rerenders components on changes
  }

  render() { // can update stuff
    return (
      <form onSubmit={this.mySubmitHandler}>
      <h1>Hi {this.state.username}</h1>
      <p>Enter your username: </p>
      <input
        type='text'
        name='username'
        onChange={this.myChangeHandler}
      />
      <p>Enter your age:</p>
      <input
        type='text'
        name='age'
        onChange={this.myChangeHandler}
      />
      <br/>
      <br/>
      <input 
        type='submit'
      />
      </form>
    );
  }
}
