import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

class Form extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        username: '',
        age: null,
      };
    }
    submitHandler = (event) => {
      event.preventDefault();

      let username = this.state.username;
      let validcharacters = '1234567890abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
      for (let i = 0, l = username.length; i < l; ++i) {
          if (validcharacters.indexOf(username.substr(i, 1)) == -1 || l <= 0) {
              alert("Username must contain only numbers and letters");
              break;
          }
      }

      let age = this.state.age;
      if (!Number(age)) {
        alert("Your age must be a number");
      }
    }
    changeHandler = (event) => {
      let nam = event.target.name;
      let val = event.target.value;
      this.setState({[nam]: val}); // rerenders components on changes
    }
    render() {
      return (
        <form onSubmit={this.submitHandler}>
        <h1>Hi {this.state.username}</h1>
        <p>Enter your name:</p>
        <input
          type='text'
          name='username'
          onChange={this.changeHandler}
        />
        <p>Enter your age:</p>
        <input
          type='text'
          name='age'
          onChange={this.changeHandler}
        />
        <br/>
        <br/>
        <input type='submit' />
        </form>
      );
    }
  }

  ReactDOM.render(<Form />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
